INSERT INTO pelicula (title, director, year, synopsis) VALUES ('Cadena perpetua', 'Frank Darabont', 1994, 'Dos hombres encarcelados forjan una amistad a lo largo de los años, encontrando consuelo y eventual redención a través de actos de decencia común.');
INSERT INTO pelicula (title, director, year, synopsis) VALUES ('El Padrino', 'Francis Ford Coppola', 1972, 'El patriarca envejecido de una dinastía del crimen organizado transfiere el control de su imperio clandestino a su reticente hijo.');
INSERT INTO pelicula (title, director, year, synopsis) VALUES ('Pulp Fiction', 'Quentin Tarantino', 1994, 'Las vidas de dos sicarios, un boxeador, un gángster y su esposa, y una pareja de bandidos de restaurante se entrelazan en cuatro historias de violencia y redención.');
INSERT INTO pelicula (title, director, year, synopsis) VALUES ('El Caballero Oscuro', 'Christopher Nolan', 2008, 'Cuando la amenaza conocida como el Joker causa estragos y caos en la gente de Gotham, Batman debe aceptar una de las mayores pruebas psicológicas y físicas de su capacidad para luchar contra la injusticia.')
INSERT INTO pelicula (title, director, year, synopsis) VALUES ('La lista de Schindler', 'Steven Spielberg', 1993, 'En la Polonia ocupada por los alemanes durante la Segunda Guerra Mundial, el industrial Oskar Schindler se preocupa gradualmente por su fuerza laboral judía después de presenciar su persecución por los nazis.')